import React, {Component} from 'react';
import {Redirect} from "react-router-dom";
import TOSNavBarBar from "../components/NavBar/TOSNavBar";
import OneEvent from "../components/OneEvent/OneEvent";
import EventCard from "../components/EventCard"
import '../App.css';
import WebsiteBKGND from '../resources/WebsiteBKGND.png';
import db from '../database/rt-base'

class Events extends Component{
    constructor(props){
        super(props)
    }

    componentDidMount() {
        let tiles = [];
        let currentComponent = this;
        db.collection("events").get().then(function(querySnapshot){
            querySnapshot.forEach(function(doc) {
                tiles.push({
                    id: doc.id,
                    title: doc.data().title,
                    content: doc.data().content,
                    img: doc.data().imgURL
                });
            });
            currentComponent.setState({tiles: tiles});
            currentComponent.setState({searchTiles: searchTiles});
        })
        .catch(function(error) {
            console.log("Error getting docs: ", error);
        });
        
    }

    render() {
        return (
            <div className="App">
                <TOSNavBarBar/>
                <div className="App-eventLayout">
                    <div className="App-events">
                        <EventCard/>
                        <OneEvent/>
                        <OneEvent/>
                        <OneEvent/>
                    </div>
                </div>
            </div>
        )
    }
}

export default Events;
